import { Text, View, FlatList, TouchableOpacity, Image } from 'react-native';
import React from 'react';
import { Svg, Path } from 'react-native-svg'; // Importa Svg y Path
import { Icon } from 'react-native-elements';
import tw from 'twrnc';
import { ArrowRightOutlined } from '@ant-design/icons';

const data = [
    {
        id: '123',
        title: 'Get a ride',
        image: 'https://links.papareact.com/3pn',
        screen: 'MapScreen'
    },
    {
        id: '456',
        title: 'Order Food',
        image: 'https://links.papareact.com/28w',
        screen: 'EatScreen'
    }
];

const NavOptions = () => {
    return (
        <View>
            <FlatList
                data={data}
                horizontal
                keyExtractor={(item) => item.id}
                renderItem={({ item }) => (
                    <TouchableOpacity style={tw`p-2 pl-6 pb-8 bg-gray-200 m-2 w-40`}>
                        <View>
                            <Image
                                style={{ width: 150, height: 150, resizeMode: 'contain' }}
                                source={{ uri: item.image }}
                            />
                        </View>
                        <Text style={tw`mt-2 text-lg font-semibold`}>{item.title}</Text>
                        {/* Usa el componente Svg para el ícono */}
                        <Svg width={20} height={20} viewBox="0 0 20 20">
    <Path
        d="M16.5 10L7 2v6H3v4h4v6l9.5-8z"
        fill="black" // Cambia el color de relleno a negro
        stroke="black" // Cambia el color del borde a negro
        strokeWidth={2} // Ancho del borde
        strokeLinecap="round" // Estilo de los extremos de la línea
        strokeLinejoin="round" // Estilo de las intersecciones de líneas
    />
</Svg>
                    </TouchableOpacity>
                )}
            />
        </View>
    );
};

export default NavOptions;
